server {
    listen       80 ;
    server_name www.example.com example.com www.netkiller.cn;
    	
    charset utf-8;
    access_log  /var/log/nginx/www.example.com.access.log;
    error_log  /var/log/nginx/www.example.com.error.log;

    if ($query_string = "") {
       set $args "";
    }

    location / {
        root /www/example.com/www.example.com;
        index index.html;
	}
}
