import os import moviepy.video.io.ImageSequenceClip
def merge_image_to_video_moviepy(folder_name): fps = 25 image_files = [] for f1 in os.listdir(folder_name): filename = os.path.join(folder_name, f1) for index in range(fps): image_files.append(filename) clip1 = moviepy.video.io.ImageSequenceClip.ImageSequenceClip(image_files, fps=fps) clip1.write_videofile("output.mp4", fps=fps) return clip1
if __name__ == '__main__': # 图片文件尺寸必须一致 folder_name = r"test/images" clip1 = merge_image_to_video_moviepy(folder_name)
