try:
    from netkiller.svg.ScalableVectorGraphics import Svg
    from netkiller.svg.elements import Text, Line, Circle, Rectangle, Image, Path, Ellipse

except ImportError as err:
    print("Error: %s" % (err))
    exit()


def main():
    svg = Svg(viewBox="0 0 10 10",
              height="120px",
              width="120px")
    # svg.link("style.css")
    svg.title("Svg 图形库")
    svg.desc("https://www.netkiller.cn")
    svg.append(Circle(cx="5", cy="5", r="4"))

    svg.script("""
    function getColor() {
      const R = Math.round(Math.random() * 255)
        .toString(16)
        .padStart(2, "0");

      const G = Math.round(Math.random() * 255)
        .toString(16)
        .padStart(2, "0");

      const B = Math.round(Math.random() * 255)
        .toString(16)
        .padStart(2, "0");

      return `#${R}${G}${B}`;
    }

    document.querySelector("circle").addEventListener("click", (e) => {
      e.target.style.fill = getColor();
    });
    """)

    svg.save('script.svg')


if __name__ == "__main__":
    main()

import os import sys
try: from netkiller.svg.ScalableVectorGraphics import Svg from netkiller.svg.elements import Text, Line, Circle, Switch
except ImportError as err: print("Error: %s" % (err)) exit()
def main(): svg = Svg(viewBox="0 -20 100 50") svg.title("Svg 图形库") svg.desc("https://www.netkiller.cn") switch = Switch(Text("@")) switch.append(Text("مرحبا", systemLanguage="ar")).append(Text("Hallo!", systemLanguage="de,nl")).append( Text("Howdy!", systemLanguage="en-us")).append(Text("Wotcha! ", systemLanguage="en-gb")).append( Text("G'day!", systemLanguage="en-au")).append(Text("Hello!", systemLanguage="en")).append( Text("Hola!", systemLanguage="es")).append(Text("Bonjour!", systemLanguage="fr")).append( Text("こんにちは", systemLanguage="ja")).append(Text("zПривет! ", systemLanguage="ru")).append( Text("你好！ ", systemLanguage="zh")) svg.append(switch) svg.save('switch.svg')
if __name__ == "__main__": main()

