section.programlisting:
import jieba import jieba.posseg as pseg import paddle words = pseg.cut("我爱北京天安门") # jieba默认模式 for word, flag in words: print('%s %s' % (word, flag))
print("="*40)
paddle.enable_static() jieba.enable_paddle() # 启动paddle模式。 words = pseg.cut("我爱北京天安门", use_paddle=True) # paddle模式 for word, flag in words: print('%s %s' % (word, flag))
