import pandas as pd
data = {'序号': pd.Series([1, 2, 3]), '姓名': ['张三', '李四', '王五']}
df = pd.DataFrame(data) df['年龄'] = [23, 25, 26] print(df)

import pandas as pd
data = {'序号': pd.Series([1, 2, 3]), '姓名': ['张三', '李四', '王五'], '年龄': [23, 25, 26] }
df = pd.DataFrame(data) print('-' * 20, "原始数据", '-' * 20) print(df) del df['年龄'] print('-' * 20, "del 删除数据", '-' * 20) print(df) print('-' * 20, "pop 删除数据", '-' * 20) df.pop('序号') print(df)
