from flask import Flask

app = Flask(__name__)

@app.route("/")
def index():  
  return """
  <h1>《Netkiller Python 手札》</h1>
  <p>这是一个运行在 Docker 容器中的演示程序.</p>
  """

if __name__ == "__main__":  
    app.run(debug=True, host='0.0.0.0')

section.programlisting:
#!/usr/bin/python3 #-*- coding: utf-8 -*- ############################################## # Home : http://netkiller.github.io # Author: Neo <netkiller@msn.com> # Upgrade: 2021-09-05 ############################################## try: import os, sys module = os.path.dirname(os.path.dirname(os.path.abspath(__file__))) sys.path.insert(0,module) from netkiller.docker import * except ImportError as err: print("%s" %(err))
nginx = Services('nginx') nginx.image('nginx:latest') nginx.container_name('nginx') nginx.restart('always') nginx.hostname('www.netkiller.cn') nginx.environment(['TA=Asia/Shanghai']) nginx.ports(['80:80'])
compose = Composes('development') compose.version('3.9') compose.services(nginx) compose.workdir('/tmp/compose')
if __name__ == '__main__': try: docker = Docker() docker.environment(compose) docker.main() except KeyboardInterrupt: print ("Crtl+C Pressed. Shutting down.")
