example.screen:
require 'watir-webdriver'
browser = Watir::Browser.new browser.goto 'http://www.google.com.hk' browser.text_field(:name => 'q').set 'netkiller' browser.button(:name => 'btnK').click
if browser.text.include? 'Neo' puts 'Text was found' else puts 'Text was not found' end browser.close()
